import React, { useState } from 'react';

const ZalopayScreens = () => {
  const [amount, setAmount] = useState(100000);
  const [orderDescription, setOrderDescription] = useState('Mô tả đơn hàng');

  const handlePayment = () => {
    fetch('https://sandbox.vnpayment.vn/paymentv2/vpcpay.html?vnp_Amount=1806000&vnp_Command=pay&vnp_CreateDate=20210801153333&vnp_CurrCode=VND&vnp_IpAddr=127.0.0.1&vnp_Locale=vn&vnp_OrderInfo=Thanh+toan+don+hang+%3A5&vnp_OrderType=other&vnp_ReturnUrl=https%3A%2F%2Fdomainmerchant.vn%2FReturnUrl&vnp_TmnCode=DEMOV210&vnp_TxnRef=5&vnp_Version=2.1.0&vnp_SecureHash=3e0d61a0c0534b2e36680b3f7277743e8784cc4e1d68fa7d276e79c23be7d6318d338b477910a27992f5057bb1582bd44bd82ae8009ffaf6d141219218625c42', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        vnp_Version: '2.0.0',
        vnp_TmnCode: '1KZAJLXY',
        vnp_Amount: amount * 100,
        vnp_Command: 'pay',
        vnp_CreateDate: new Date().toISOString(),
        vnp_CurrCode: 'VND',
        vnp_Locale: 'vn',
        vnp_OrderInfo: orderDescription,
        vnp_OrderType: 'topup',
        vnp_ReturnUrl: 'http://localhost:3000/',
        vnp_TxnRef: 'https://sandbox.vnpayment.vn/paymentv2/vpcpay.html',
        vnp_IpAddr: 'IP_ADDRESS_OF_USER',
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        window.location = data.data;
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Thanh toán VNPay</h2>
      <p>Số tiền: {amount} VND</p>
      <p>Mô tả đơn hàng: {orderDescription}</p>
      <button onClick={handlePayment}>Thanh toán</button>
    </div>
  );
};

export default ZalopayScreens;
