import React, { useState } from 'react';

const ZalopayScreens = () => {
 const  handleSandboxPayment = async () => {
    try {
      // Thông tin cần thiết từ tài khoản Zalo Pay Sandbox
      const app_id = '553';
      const key1 = '9phuAOYhan4urywHTh0ndEXiV3pKHr5Q';
      const key2 = 'Iyz2habzyr7AG8SgvoBCbKwKi3UzlLi3';
      const endpoint = 'https://sandbox.zalopay.com.vn/api/v2/create';
      
      // Tạo đối tượng ZaloPaySDK
      const zaloPay = new ZaloPaySDK(app_id, key1, endpoint, key2);
      
      // Tạo dữ liệu đơn hàng thử nghiệm
      const data = {
        appid: app_id,
        apptransid: 'TEST123',
        appuser: 'user123',
        appdata: 'Test data',
        amount: 10000, // Số tiền thanh toán thử nghiệm
        description: 'Thanh toán thử nghiệm',
      };
      
      // Tạo mã QR code hoặc URL thanh toán
      const qrCodeUrl = zaloPay.createQRCode(data);
      
      // Redirect người dùng đến URL thanh toán
      window.location.href = qrCodeUrl;
    } catch (error) {
      console.error('Lỗi khi thực hiện thanh toán ZaloPay Sandbox', error);
    }
  }
  return (
    <div>
    <h1>Chọn ZaloPay Sandbox để thanh toán</h1>
    <button onClick={handleSandboxPayment}>Thanh toán với ZaloPay Sandbox</button>
    </div>
  );
};

export default ZalopayScreens;
